import React, { Fragment, useContext } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../../../context/auth/AuthContext';
import PropTypes from 'prop-types';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import DropDownMenu from '../../Layout/DropdownMenu/DropdownMenu.js';
import './Navbar.css';


const Navbar = (props) => {
    const authContext = useContext(AuthContext);
    const { isAuthenticated, logout, user } = authContext;

    const onLogout = () => {
        logout();
    };

    const authLinks = (
        <div className="right">
            <li className="tom">Hello {user && user.name}</li>
            <li>
                <a onClick={onLogout} href='#!'>
                    <span>Logout</span>
                </a>
            </li>
        </div>
    );

    const guestLinks = (
        <Fragment>
            <div className="right">
                <li>
                    <Link to='/register'>Register</Link>
                </li>
                <li>
                    <Link to='/login'>Login</Link>
                </li>
            </div>
        </Fragment>
    );

    const loginNav = <Route exact path='/login' component={LoginPage} key={LoginPage.name} />;

    const navbarBurger = () => {
        if (Route === loginNav) {
            <DropDownMenu />
        }
    }

    return (
        <div>
            <nav className="nav-wrapper blue lighten-1 ">
                <div></div>
                <div>
                    <a href="#!" className="brand-logo center"> {isAuthenticated ? "Dashboard" : props.appName} </a>
                </div>
                <div>
                    <ul>{isAuthenticated ? authLinks : guestLinks}</ul>
                    <ul>{navbarBurger}</ul>
                </div>
            </nav>
        </div>

    )
}
Navbar.propTypes = {
    appName: PropTypes.string.isRequired

};

Navbar.defaultProps = {
    appName: 'TimeCardApp'
};

export default Navbar;
