import React, { useState, useContext } from 'react';
import './LoginForm.css';
import AuthContext from '../../../context/auth/AuthContext';
import { Link, withRouter } from 'react-router-dom';
import setAuthToken from '../../../utils/setAuthToken';

const LoginForm = props => {

    const authContext = useContext(AuthContext);

    const { login } = authContext;

    if (localStorage.token) {
        setAuthToken(localStorage.token);
        props.history.push('/');
    }


    const [user, setUser] = useState({
        email: '',
        password: ''
    });

    const { email, password } = user;

    const onChange = e => setUser({ ...user, [e.target.name]: e.target.value });

    const onSubmit = e => {
        e.preventDefault();

        login({
            email,
            password
        });

    };



    return (

        <div className='form-container'>

            <style>
                {/* @import url('https://fonts.googleapis.com/css?family=IBM+Plex+Sans:400i&display=swap'); */}
                @import url('https://fonts.googleapis.com/css?family=IBM+Plex+Sans:400,400i&display=swap');
            </style>

            <h1>
                <span className='loginHeader'>Login</span>
            </h1>
            <form onSubmit={onSubmit}>
                <div className='form-group'>
                    <label htmlFor='email'>Email Address</label>
                    <input
                        type='email'
                        name='email'
                        value={email}
                        onChange={onChange}
                        required
                    />
                </div>
                <div className='form-group'>
                    <label htmlFor='password'>Password</label>
                    <input
                        type='password'
                        name='password'
                        value={password}
                        onChange={onChange}
                        required
                    />
                </div>
                <input
                    type='submit'
                    value='Login'
                    className='btn btn-primary btn-block blue lighten-1'
                />
            </form>
            <div className="center">
                <Link id="ForgotPassword" to="/forgotmypassword">Forgot My Password  </Link> | <Link id="CreateAccount" to="/register">  Create Account</Link>
            </div>
        </div>

    );
};

export default withRouter(LoginForm);
